				// See http://propel.phpdb.org/trac/ticket/509
				// $obj1->hydrate($row, 0, true); // rehydrate
			} else {
				$cls = TagsPhotoPeer::getOMClass(false);

				$obj1 = new $cls();
				$obj1->hydrate($row);
				TagsPhotoPeer::addInstanceToPool($obj1, $key1);
			} // if obj1 already loaded

			// Add objects for joined Photo rows

			$key2 = PhotoPeer::getPrimaryKeyHashFromRow($row, $startcol2);
			if ($key2 !== null) {
				$obj2 = PhotoPeer::getInstanceFromPool($key2);
				if (!$obj2) {

					$cls = PhotoPeer::getOMClass(false);

					$obj2 = new $cls();
					$obj2->hydrate($row, $startcol2);
					PhotoPeer::addInstanceToPool($obj2, $key2);
				} // if obj2 loaded

				// Add the $obj1 (TagsPhoto) to the collection in $obj2 (Photo)
				$obj1->setPhoto($obj2);
			} // if joined row not null

			$results[] = $obj1;
		}
		$stmt->closeCursor();
		return $results;
